        /// <summary>
        /// Enumeration of valid key mods (possibly OR'd together).
        /// </summary>
        public enum SDL_KeyMod : ushort
        {
            None = 0x0000,
            LeftShift = 0x0001,
            RightShift = 0x0002,
            LeftControl = 0x0040,
            RightControl = 0x0080,
            LeftAlt = 0x0100,
            RightAlt = 0x0200,
            LeftGUI = 0x0400,
            RightGUI = 0x0800,
            Num = 0x1000,
            Caps = 0x2000,
            Mode = 0x4000,
            Reserved = 0x8000,
            Control = LeftControl | RightControl,
            Shift = LeftShift | RightShift,
